behavior.onClick:
// Toggle LED color and state
const colors = ['Red', 'Green', 'Blue', 'Yellow', 'White'];
const currentColor = context.getCellByPosition(1, 0).properties?.color || 'Red';
const currentIndex = colors.indexOf(currentColor);
const nextIndex = (currentIndex + 1) % colors.length;
const newColor = colors[nextIndex];

// Update LED color properties
const colorMap = {
  'Red': { voltage: 2.0, background: '#EF4444', border: '#DC2626', offBackground: '#7F1D1D' },
  'Green': { voltage: 2.2, background: '#10B981', border: '#059669', offBackground: '#064E3B' },
  'Blue': { voltage: 3.3, background: '#3B82F6', border: '#2563EB', offBackground: '#1E3A8A' },
  'Yellow': { voltage: 2.1, background: '#F59E0B', border: '#D97706', offBackground: '#92400E' },
  'White': { voltage: 3.3, background: '#F3F4F6', border: '#9CA3AF', offBackground: '#6B7280' }
};

const colorProps = colorMap[newColor];

// Update the LED body cell (middle cell)
const ledBody = context.getCellByPosition(1, 0);
if (ledBody) {
  // Toggle between on and off states
  const isCurrentlyOn = ledBody.isPowered || false;
  const newState = !isCurrentlyOn;

  // Update power state
  context.updateCellPower(1, 0, newState);

  console.log('LED color changed to:', newColor, 'Voltage:', colorProps.voltage + 'V', 'State:', newState ? 'ON' : 'OFF');
}
