behavior.onClick:
// Cycle through common resistance values
const currentResistance = context.getCellByPosition(1, 0).resistance || 1000;
const commonValues = [10, 22, 47, 100, 220, 470, 1000, 2200, 4700, 10000, 22000, 47000, 100000, 220000, 470000, 1000000, 2200000, 4700000, 10000000];
const currentIndex = commonValues.indexOf(currentResistance);
const nextIndex = (currentIndex + 1) % commonValues.length;
const newResistance = commonValues[nextIndex];

// Update resistance for all cells in the resistor
for (let dx = 0; dx < 3; dx++) {
  const cell = context.getCellByPosition(dx, 0);
  if (cell) {
    cell.resistance = newResistance;
  }
}

// Show resistance value in console
const displayValue = newResistance >= 1000 ? `${newResistance / 1000}kΩ` : `${newResistance}Ω`;
console.log('Resistor resistance changed to:', displayValue);

// Trigger a visual update
context.forceUpdate();
